(window.webpackJsonp=window.webpackJsonp||[]).push([[318],{1015:function(t,a,v){"use strict";v.r(a);var _=v(1),s=Object(_.a)({},(function(){var t=this,a=t.$createElement,v=t._self._c||a;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h3",{attrs:{id:"自我介绍"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#自我介绍"}},[t._v("#")]),t._v(" 自我介绍")]),t._v(" "),v("p",[t._v("原文："),v("a",{attrs:{href:"https://www.yoodb.com/quotation/detail/1134",target:"_blank",rel:"noopener noreferrer"}},[t._v("素文宅博客"),v("OutboundLink")],1)]),t._v(" "),v("div",{staticClass:"custom-block tip"},[v("p",{staticClass:"custom-block-title"},[t._v("提示")]),t._v(" "),v("p",[t._v("在企业面试环节中“自我介绍”这个老生常谈的话题就不用多说什么了，面试官必定会问的。那么如何在自我介绍的时候就能够打动面试官，吸引面试官对面试者的兴趣？如何进行自我介绍比较好？有没有什么方式方法呢？当然是有的。")])]),t._v(" "),v("h4",{attrs:{id:"为什么面试官让面试者自我介绍"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#为什么面试官让面试者自我介绍"}},[t._v("#")]),t._v(" 为什么面试官让面试者自我介绍")]),t._v(" "),v("ol",[v("li",[t._v("面试官想对面试者有个初步的了解；")]),t._v(" "),v("li",[t._v("面试官争取在仅有的时间内快速浏览简历；")]),t._v(" "),v("li",[t._v("面试官通过让面试者自我介绍来缓冲一下面试气氛，使面试者不过于太紧张影响其发挥能力。")])]),t._v(" "),v("h4",{attrs:{id:"自我介绍套话"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#自我介绍套话"}},[t._v("#")]),t._v(" 自我介绍套话")]),t._v(" "),v("ol",[v("li",[t._v("面试官您好，我叫素文宅博客网站（面试者姓名），本科毕业（211或985重点突出一下），来自于Java精选公众号（省份、市区）。从网上看到公司招聘的这个职位，觉得自己比较适合公司的岗位，对自己的发展也有帮助，所以来这里争取下这份工作。")]),t._v(" "),v("li",[t._v("核心竞争力（最擅长的技能、最深入研究的知识领域、最积极的部分）和闪光点（做过最成功的事，主要的成就）。核心就是要体现自己胜任这份工作（介绍时候要自然有底气，合情合理，面试前要充分准备好）。\n"),v("ul",[v("li",[v("strong",[t._v("初级Java工程师")]),t._v(":\n面试初级Java工程师岗位时，首先应该重点突出一下自身的知识结构，因为面试初级岗位的应聘者往往没有实际工作经验，因此要强调自身的知识结构体系。如果是计算机相关专业的应届生，那么一定要首先突出描述自己的学历背景；因为学历背景是自身知识结构一个有力的"),v("code",[t._v("敲门砖")]),t._v("，如果是非计算机专业的毕业生，则要重点突出说一下自身的学习经历，强调自己的学习能力，与此同时突出一下自己的动手和实践能力。最后不管是计算机专业还是非计算机专业都要重点描述一下掌握了哪些Java技术，比如开源框架、数据库、大数据、消息队列、缓存等等。\n总的来说，初级Java工程师岗位的面试需要在自我介绍中突出三点内容，其一是自身的知识结构；其二是独立开发能力；其三是学习能力，这三个方面的表述往往能够决定最终的面试结果。")]),t._v(" "),v("li",[v("strong",[t._v("中高级Java工程师")]),t._v(":\n面试中高级Java工程师岗位时，首先应该重点强调一下自己的项目开发经验，从技术选型、方案设计到具体编码都要有一个清晰的思路来描述，在描述过程中要把自己的开发经验突出一下，比如经历了哪些不同的开发场景，解决了哪些疑难问题等，如果具有较多的行业经验，也一定要突出一下（多行业经验有时未必是好事，建议挑重点行业或者面试企业对口行业）。\n面试中高级Java工程师岗位时虽然比较看重经验，但同时也比较看重应聘者的沟通交流能力，因为不少开发问题说到底就是沟通不清楚导致的问题，沟通能力较强的程序员往往能够更高效地完成工作任务。")])])]),t._v(" "),v("li",[t._v("先介绍到这里，面试官看看您还有什么问题，我可以再补充。")]),t._v(" "),v("li",[t._v("在最后自我介绍结束时要说谢谢，任何面试官都喜欢有礼貌的人！\n注意：切记不要自曝其短（说自己的缺点），在进行自我介绍的时候千万不要和简历有冲突！（有的会说简历未更新，那也是面试者自身问题）")])]),t._v(" "),v("h3",{attrs:{id:"面试官必问"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#面试官必问"}},[t._v("#")]),t._v(" 面试官必问")]),t._v(" "),v("h4",{attrs:{id:"你有什么优劣势"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#你有什么优劣势"}},[t._v("#")]),t._v(" 你有什么优劣势？")]),t._v(" "),v("ol",[v("li",[t._v("优点方面尽量突出一些的（技术痴迷、学习能力强、为人谦和），但要注意不要浮夸吹嘘，那样反而把优点变成了缺点。")]),t._v(" "),v("li",[t._v("劣势方面不是计算机专业可以（非计算机专业人员），但是对技术很是痴迷。关注公众号“Java精选”更多关于面试技巧、面试经验分享。")])]),t._v(" "),v("div",{staticClass:"custom-block warning"},[v("p",{staticClass:"custom-block-title"},[t._v("注意事项")]),t._v(" "),v("p",[t._v("面试时面试官谈及个人缺点，最好的办法还是坦然地承认它，为自己的缺点辩驳也无济于事，重要的是如何让别人在感情上认同你谈及自身缺点的态度。")])]),t._v(" "),v("h4",{attrs:{id:"你为什么选择我们公司"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#你为什么选择我们公司"}},[t._v("#")]),t._v(" 你为什么选择我们公司？")]),t._v(" "),v("ol",[v("li",[t._v("面试前，应对应聘公司各方面，要了解详尽！至少要了解到行业、企业、岗位这三方面！最好要准备一些具体的数据和实例！")]),t._v(" "),v("li",[t._v("面试时，可以将之前对公司的了解（行业、企业、数据、实例）结合自己的JAVA岗位，大略的说一遍，然后强调自己“觉得这个工作合适”即可。")]),t._v(" "),v("li",[t._v("面试者如果对面试公司所属行业确实很喜欢，要说出自身对这个行业的认知程度。")])]),t._v(" "),v("h3",{attrs:{id:"面试者必问"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#面试者必问"}},[t._v("#")]),t._v(" 面试者必问")]),t._v(" "),v("div",{staticClass:"custom-block tip"},[v("p",{staticClass:"custom-block-title"},[t._v("提示")]),t._v(" "),v("p",[t._v("面试是个双向选择，公司发布岗位招聘，标明工资福利。通过一系列面试来筛选出符合自己要求的员工。工人选择岗位招聘，通过一系列面试来获得工作进入职场，上班拿薪水生活。当公司通过一系列的一轮、二轮、...n轮面试来选择员工的时候，我们作为应聘者也应该考量这份工作除了表明的薪水还能给我们带来什么？")])]),t._v(" "),v("ol",[v("li",[v("strong",[t._v("薪资组成")]),t._v("\n极少的基础工资加上极高的绩效这种最好慎重、因为加班费大多是按照基础工资算的。五险一金也是按照基础工资为基数，在从当地的可选比例里面选一个缴纳的。")]),t._v(" "),v("li",[v("strong",[t._v("五险一金比例")]),t._v("\n不同的地区缴纳比例大多不一样，没有定死的。大多是一个区间，比如公积金缴纳比例 5%-12% 这种，如果你的公司给你缴纳的公积金比例是取 5% 的话，那么你应该慎重选择了。像这么抠的，进去后绝对还能刷新你的下线。")]),t._v(" "),v("li",[v("strong",[t._v("技术架构")]),t._v("\n这点对于从事技术线发展的人而言，尤其重要。技术尽管并不是越新越好，一切的技术最终都是落于业务产生经济效益才是有意义的，但是如果对方的公司目前技术方案落后于主流十几二十年，直接就意味着工作上的技术出了这家公司是没人看的上的。此时个人的技术成长基本上就必须是抛开上班时间了。java招聘一般工作经验与工资是成正比例关系的，但有个前提，技术得达标。")]),t._v(" "),v("li",[v("strong",[t._v("成长体系")]),t._v("\n不同公司职位划分，职称评定都不一样。一样的是，如果面试官都说不清的公司成长体系，那么不用想，公司的人事变动基本上就全是离职、入职。甚少有职位上升。")]),t._v(" "),v("li",[v("strong",[t._v("入职负责业务方向")]),t._v("\n以上都满意了，此时就要考虑将来工作做什么？如果此时入职所接触的业务与上家的差不多，那么自然入职顺畅许多，甚至关系处理的好的话可以申请提前转正。但是如果接触的是全新的业务的话，此时就要做好心里准备，必定需要花大量的时间来学习，免不了要不停的询问公司的老员工。")])])])}),[],!1,null,null,null);a.default=s.exports}}]);